CREATE FUNCTION public.get_rapport_moisson(moisson_uuid uuid) RETURNS TABLE(moisson_id uuid, theme character varying, date_moisson date, objectif numeric, total_collecte numeric, reste_a_collecter numeric, supplement numeric, pourcentage_realise numeric, statut_global character varying, passages_json jsonb, total_passages numeric, ventes_json jsonb, total_ventes numeric, engagements_json jsonb, total_engagements numeric, engagements_en_attente integer)
    LANGUAGE plpgsql
    AS $$
            DECLARE
                moisson_exists BOOLEAN := FALSE;
            BEGIN
                -- Vérifier l'existence de la moisson
                SELECT EXISTS(SELECT 1 FROM moissons WHERE id = moisson_uuid AND deleted_at IS NULL)
                INTO moisson_exists;

                IF NOT moisson_exists THEN
                    RAISE EXCEPTION 'Moisson avec ID % introuvable', moisson_uuid;
                END IF;

                RETURN QUERY
                SELECT
                    m.id AS moisson_id,
                    m.theme,
