CREATE VIEW public.contacts_publics AS
 SELECT c.id,
    c.nom_eglise,
    c.type_contact,
    c.telephone_principal,
    c.email_principal,
    c.adresse_complete,
    c.ville,
    c.site_web_principal,
    c.facebook_url,
    c.instagram_url,
    c.horaires_cultes,
    c.latitude,
    c.longitude,
    concat(resp.prenom, ' ', resp.nom) AS nom_responsable
   FROM (public.contacts c
     LEFT JOIN public.users resp ON ((c.responsable_contact_id = resp.id)))
  WHERE ((c.visible_public = true) AND (c.deleted_at IS NULL))
  ORDER BY c.type_contact, c.nom_eglise;


