        CASE
            WHEN (m.montant_solde >= m.cible) THEN 'Objectif atteint'::text
            WHEN (m.montant_solde >= (m.cible * 0.9)) THEN 'Presque atteint'::text
            WHEN (m.montant_solde >= (m.cible * 0.7)) THEN 'Bonne progression'::text
            WHEN (m.montant_solde >= (m.cible * 0.5)) THEN 'En cours'::text
            WHEN (m.montant_solde >= (m.cible * 0.3)) THEN 'Début'::text
            ELSE 'Très faible'::text
