--

CREATE VIEW public.engagements_en_retard AS
 SELECT em.id,
    em.moisson_id,
    m.theme AS theme_moisson,
    m.date AS date_moisson,
    em.categorie,
        CASE
            WHEN ((em.categorie)::text = 'entite_morale'::text) THEN em.nom_entite
            ELSE 'Personne physique'::character varying
        END AS donateur,
    em.cible,
    em.montant_solde,
    em.reste,
    em.date_echeance,
    em.date_rappel,
    em.telephone,
    em.email,
    (CURRENT_DATE - em.date_echeance) AS jours_retard,
        CASE
            WHEN (em.date_echeance < (CURRENT_DATE - '30 days'::interval)) THEN 'Critique'::text
            WHEN (em.date_echeance < (CURRENT_DATE - '15 days'::interval)) THEN 'Important'::text
            WHEN (em.date_echeance < (CURRENT_DATE - '7 days'::interval)) THEN 'Modéré'::text
            ELSE 'Récent'::text
        END AS niveau_urgence
   FROM (public.engagement_moissons em
     JOIN public.moissons m ON ((em.moisson_id = m.id)))
  WHERE ((em.deleted_at IS NULL) AND (m.deleted_at IS NULL) AND (em.status = true) AND (em.reste > (0)::numeric) AND (em.date_echeance < CURRENT_DATE))
  ORDER BY em.date_echeance;


